import * as React from "react";
import {useEffect} from 'react';
import { NavigationContainer } from "@react-navigation/native";
import {
  createDrawerNavigator,
  DrawerContentScrollView,
} from "@react-navigation/drawer";
import {
  NativeBaseProvider,
  Box,
  Pressable,
  VStack,
  Text,
  HStack,
  Divider,
} from "native-base";
const Drawer = createDrawerNavigator();
import NewRecord from './screens/NewRecord';
import openDatabase from "./Common/DB";



function SideMenu() {
    const db = openDatabase();
    useEffect(() => {
      db.transaction((tx) => {
        tx.executeSql("create table if not exists catalogs (id integer primary key not null, name text, UNIQUE(name));");
        tx.executeSql("create table if not exists records (id integer primary key not null, name text, value real, catalog_id interger not null, FOREIGN KEY (catalog_id) references catalog (id));");
        tx.executeSql("insert OR IGNORE into catalogs (name) values ('Transport');");
        tx.executeSql("insert OR IGNORE into catalogs (name) values ('Food');");
        tx.executeSql("insert OR IGNORE into catalogs (name) values ('Entertainment');");
        tx.executeSql("insert OR IGNORE into catalogs (name) values ('Living');");
        tx.executeSql("select * from catalogs", [], (_, { rows }) =>
          console.log(JSON.stringify(rows))
        );
      });
    }, []);

  return (
    <Box safeArea flex={1}>
      <Drawer.Navigator
        drawerContent={(props) => <SideMenuContent  {...props} />}
      >
        <Drawer.Screen name="New Record" component={NewRecord} />
      </Drawer.Navigator>
    </Box>
  );
}
export default function App() {
  return (
    <NavigationContainer>
      <NativeBaseProvider>
        <SideMenu />
      </NativeBaseProvider>
    </NavigationContainer>
  );
}

function SideMenuContent(props) {
  return (
    <DrawerContentScrollView {...props} safeArea>
      <VStack space="6" my="2" mx="1">
        <VStack divider={<Divider />} space="4">
          <VStack space="3">
            {props.state.routeNames.map((name, index) => (
              <Pressable
                px="5"
                py="3"
                rounded="md"
                key={index}
                bg={
                  index === props.state.index
                    ? "rgba(6, 182, 212, 0.1)"
                    : "transparent"
                }
                onPress={(event) => {
                  props.navigation.navigate(name);
                }}
              >
                <HStack space="7" alignItems="center">
                  <Text
                    fontWeight="500"
                    color={
                      index === props.state.index ? "primary.500" : "gray.700"
                    }
                  >
                    {name}
                  </Text>
                </HStack>
              </Pressable>
            ))}
          </VStack>
        </VStack>
      </VStack>
    </DrawerContentScrollView>
  );
}
